var UI={site:{width:document.documentElement.clientWidth,height:$(".container").height(),breakpoints:[{name:"medium",min:767},{name:"small",min:0}],mode:{}},init:function(){$(window).bind("resize",function(){UI.site.width=document.documentElement.clientWidth;UI.getSiteMode();UI.site.mode=="small"?UI.resetMobileNav():UI.resetDesktopNav()});UI.getSiteMode();$(".toggle-main-nav").bind("click",function(e){$(this).toggleClass("toggle-menu--open");$(".main-nav").toggle();return!1});$(".toggle-search").bind("click",function(e){$(this).toggleClass("toggle-search--active");$(".search-form").toggle().toggleClass("search-form--open");$(".main-nav").toggle();return!1});$(".search__keywords").bind("keyup",function(){var e=$(".search-prompt");$(this).val().length>0?e.show():e.hide()});$(".library__item").bind("mouseover",function(e){e.stopPropagation();$(this).addClass("library__item--active")});$(".library__item").bind("mouseout",function(e){e.stopPropagation();$(this).removeClass("library__item--active")})},getSiteMode:function(){for(var e=0,t=UI.site.breakpoints.length;e<t;e++){var n=UI.site.breakpoints[e];if(document.documentElement.clientWidth>n.min){UI.site.mode=n.name;break}}},resetMobileNav:function(){$(".toggle-main-nav").removeClass("toggle-menu--open");$(".main-nav").hide();UI.resetSearchBar()},resetDesktopNav:function(){$(".main-nav").show();UI.resetSearchBar()},resetSearchBar:function(){$(".toggle-search").removeClass("toggle-search--active");$(".search-form").hide()}};$(document).ready(function(){UI.init()});